INSERT INTO `ktth_ask` VALUES (1,2,'<p>\r\n	Những kh&oacute; khăn bắt đầu ập tới với con từ khi con vừa học xong trung học.</p>\r\n','Phật tử','2012-11-21 00:15:23','<p>\r\n	Xin ch&agrave;o người bạn trẻ th&acirc;n mến! C&aacute;m ơn rất nhiều những chia sẻ từ tr&aacute;i tim của bạn. Tất cả những sự chia sẻ đ&oacute; cũng đều xuất ph&aacute;t từ sự mong muốn c&oacute; một hướng đi để c&oacute; thể sống l&agrave;nh, sống đẹp, sống b&igrave;nh an v&agrave; hạnh ph&uacute;c.</p>\r\n','BBT:',''),(2,2,'<p>\r\n	Sự mong muốn th&agrave;nh c&ocirc;ng đ&atilde; mang đến qu&aacute; nhiều đau khổ v&agrave; bận rộn. Kh&ocirc;ng điều g&igrave; con l&agrave;m m&agrave; cảm thấy đủ cả. L&agrave;m thế n&agrave;o con c&oacute; thể tạo sự an lạc cho bản th&acirc;n?</p>\r\n','Phật tử','2012-11-21 00:15:35','<p>\r\n	Phẩm chất của h&agrave;nh động của bạn phụ thuộc v&agrave;o phẩm chất sự c&oacute; mặt của bạn. Đề nghị bạn hăng h&aacute;i để hiến tặng hạnh ph&uacute;c, l&agrave;m cho người kh&aacute;c hạnh ph&uacute;c. Đ&oacute; l&agrave; việc l&agrave;m tốt đẹp. Nếu bạn kh&ocirc;ng hạnh ph&uacute;c, bạn sẽ kh&ocirc;ng thể l&agrave;m điều đ&oacute;.</p>\r\n','BBT:',''),(3,2,'<p>\r\n	Bất cứ khi n&agrave;o t&ocirc;i l&agrave;m điều g&igrave;, t&acirc;m tr&iacute; t&ocirc;i cứ hướng về tương lai cho việc suy nghĩ?</p>\r\n','Phật tử','2012-11-21 00:16:03','<p>\r\n	Khi bạn nhận c&ugrave;ng l&uacute;c nhiều bức thư, bạn phải quyết định xem n&ecirc;n đọc bức n&agrave;o trước. Tuy bạn muốn đọc hai bức thư c&ugrave;ng một l&uacute;c bởi ch&uacute;ng đều quan trọng.</p>\r\n','BBT:',''),(4,1,'<p>\r\n	L&agrave;m thế n&agrave;o để c&oacute; sự tự tin nơi ch&iacute;nh m&igrave;nh?</p>\r\n','Phật tử','2012-11-21 00:16:36','<p>\r\n	C&acirc;u trả lời n&agrave;y do Ban bi&ecirc;n tập ghi lại t&oacute;m tắt lời dạy của Sư &Ocirc;ng. Khi nh&igrave;n s&acirc;u v&agrave;o ch&iacute;nh m&igrave;nh ta thấy bản th&acirc;n m&igrave;nh vốn c&oacute; sẵn một gia t&agrave;i rất lớn.</p>\r\n','BBT:',''),(5,1,'<p>\r\n	K&iacute;nh thưa qu&yacute; thầy v&agrave; qu&yacute; sư c&ocirc;! Mỗi khi c&oacute; t&acirc;m h&agrave;nh ti&ecirc;u cực xuất hiện, con rất muốn chuyển h&oacute;a ch&uacute;ng.</p>\r\n','Phật tử','2012-11-21 00:16:46','<p>\r\n	Th&acirc;n ch&agrave;o bạn! Mỗi khi bạn c&oacute; những t&acirc;m h&agrave;nh ti&ecirc;u cực xuất hiện, th&igrave; việc đầu ti&ecirc;n m&agrave; bạn phải n&ecirc;n l&agrave;m l&agrave; thực tập hơi thở c&oacute; ch&aacute;nh niệm, để đưa t&acirc;m trở về với th&acirc;n ngay trong gi&acirc;y ph&uacute;t hiện tại.</p>\r\n','BBT:',''),(6,1,'<p>\r\n	Con l&agrave; một sinh vi&ecirc;n vừa tốt nghiệp đại học. Con phải l&agrave;m g&igrave; b&acirc;y giờ?</p>\r\n','Phật tử','2012-11-21 00:16:57','<p>\r\n	Ban Bi&ecirc;n Tập L&agrave;ng Mai đ&atilde; nhận được thư bạn v&agrave; nhiều bức thư kh&aacute;c gởi về với nội dung: muốn t&igrave;m một nơi n&agrave;o m&agrave; ở đ&oacute; m&igrave;nh về lại với ch&iacute;nh m&igrave;nh, ở đ&oacute; m&igrave;nh cảm nhận được cảm gi&aacute;c AN LẠC trong cuộc sống n&agrave;y m&agrave; kh&ocirc;ng phải mưu t&iacute;nh cho tương lai m&agrave; đ&acirc;u biết m&igrave;nh đang hiện hữu trong hiện tại.</p>\r\n','BBT:',''),(7,1,'<p>\r\n	Con biết rằng thế giới n&agrave;y c&oacute; nhiều ph&aacute;p m&ocirc;n tu v&agrave; đạo Phật lu&ocirc;n hướng con người đến con đường an lạc.</p>\r\n','Phật tử','2012-11-21 00:17:16','<p>\r\n	Bạn Anh Nhi th&acirc;n mến! Đọc những d&ograve;ng t&acirc;m sự của bạn, trong t&ocirc;i c&oacute; sự cảm th&ocirc;ng. Ng&agrave;y xưa, khi t&ocirc;i bắt đầu c&oacute; ước muốn đi xuất gia để tiếp nối con đường rất đẹp, rất s&aacute;ng của Bụt.</p>\r\n','BBT:',''),(15,2,'<p>\r\n	Con Bi cam phai lam sao</p>\r\n','Thanh Phong','2013-04-23 13:35:04','<p>\r\n	Tra loi ...............uong thuoc</p>\r\n','BBT',''),(16,2,'thử đặt câu hỏi','abc','2013-07-02 00:39:55','','',''),(17,2,'','','2013-07-20 08:22:59','','','');
